options.body:
{% spaceless %}
    {% set must = [] %}
    {% set body = {
        "sort": [
            {
              "_finalization_datetime": {
                "order": "desc"
              }
            }
        ] 
    } %}
    
    {% if app.request.query.get('q', false) %}
        {% set must = must|merge({ 
            "query_string": {
                "query": app.request.query.get('q')|default('*'),
                "default_field": app.request.query.get('fields')|default('_all')
            } 
        }) %}
    {% endif %}
    
    {% if must %}{% set body = body|merge({'query': { "bool": { "must": must } } } ) %}{% endif %}
{% endspaceless %}
{{- body|json_encode|raw -}}

options.template:
{% set tableLength = 200 %}
{% set environments = ['template', 'preview', 'live'] %}
{% set config = 'config'|i18n('en')|json_decode %}

{% set published = [] %}
{% for env in environments %}
    {% set envHits = {
        "index": 'preview'|get_environment.alias,
        "size": 3000,
        "_source": ['_sha1'],
        "body": {"query": {"term": { "_contenttype": { "value": "#{contentType.name}" } } } }
    }|search.hits.hits %}
    {% set envIdSha1 = [] %}
    {% for hit in envHits %}
        {% set envIdSha1 = envIdSha1|merge({ (hit._id): (hit._source._sha1) }) %}
    {% endfor %}
    {% set published = published|merge({ (env): (envIdSha1) }) %}
{% endfor %}

<form method="GET" class="form">
    <div class="well" style="padding: 15px;">
        <div class="row">
            <div class="col-md-4">
                <select class="form-control input-sm" name="fields">
                    <option value="_all" {% if app.request.query.get('fields')|default(false) == "_all" %}selected{% endif %}>All fields</option>
                    {% for label, locale in config.locales %}
                        <option value="{{ "all_#{locale}" }}" {% if app.request.query.get('fields')|default(false) == "all_#{locale}" %}selected{% endif %}>{{ "Fields in #{label}" }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <input type="text" class="form-control input-sm" placeholder="Search text (abc*)" name="q" value="{{ app.request.query.get('q')|default('') }}">
            </div>
            <div class="col-md-4">
                <div>
                    <div class="btn-group">
                        <button type="submit" class="btn btn-sm btn-primary">Search</button>
                        <a href="{{ path('data.customindexview', { viewId: view.id}) }}" class="btn  btn-sm btn-primary">Reset</a>
                    </div>
                    {% if is_granted(contentType.createRole) %}
                        <a href="{{ path('data.add', {contentType: contentType.id}) }}" class="btn btn-sm btn-success pull-right"><i class="fa fa-plus"></i>&nbsp;Add </a>        
                    {% endif%}
                </div>
            </div>
       </div>
    </div>
</form>

<div class="table-responsive">
    <table id="overview" class="table table-bordered table-striped" data-page-length="{{ tableLength }}" >
        <thead>
            <tr>
                {% if is_granted(contentType.editRole) %}<th style="width: 20px;" data-orderable="false"></th>{% endif %}
                <th>key</th>
                <th class="text-center">languages</th>
                <th class="text-center">published</th>
                <th class="text-center" style="width: 20px;">info</th>
                {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}<th data-orderable="false" style="width: 60px;"></th>{% endif %}
            </tr>
        </thead>
        <tbody>
            {% for hit in result.hits.hits %}
                {% set info %}
                   {{ hit._source._finalization_datetime|date('d-m-Y H:i') }}
                   <br />
                   {{ hit._source._finalized_by is defined ? hit._source._finalized_by : '' }}
                {% endset %}
                <tr>
                    {% if is_granted(contentType.editRole) %}
                        <td><a class="btn btn-xs btn-primary" href="{{ path('revision.new-draft', {'type': contentType.name, 'ouuid': hit._id }) }}"><i class="fa fa-pencil" /></a></td>
                    {% endif %}
                    <td><a href="{{ path('ems_content_revisions_view', {'type': contentType.name, 'ouuid': hit._id }) }}" >{{ hit._source.key }}</a></td>
                    <td class="text-center" 
                        data-order="{{ config.locales|filter(v => attribute(hit._source, "label_#{v}")|default("") != "")|join(' ') }}"
                        data-search="{{ config.locales|map(v => attribute(hit._source, "label_#{v}")|default("") )|join(' ') }}">
                        {% for lang, locale in config.locales %}
                            {% set langClass = attribute(hit._source, "label_#{locale}")|default("") != "" ? 'primary': 'default' %}
                            <span class="label {{ "label-#{langClass}"|default("") }}" data-toggle="tooltip" data-placement="top" aria-hidden="true" title="{{ attribute(hit._source, "label_#{locale}")|default("") }}">{{ locale|upper }}</span>
                        {% endfor %}
                    </td>
                    <td class="text-center" data-order="{{ environments|filter(v => hit._id in attribute(published, v)|keys)|join(' ') }}">
                        <div class="btn-group">
                            {% for env in environments %}
                                {% set isPublished = (hit._id in attribute(published, env)|keys ? true : false) %}
                                {% set isAligned = (hit._source._sha1 == attribute(attribute(published, env), hit._id)|default(false) ? true : false) %}
                                {% set envClass = (isAligned ? 'primary' : (isPublished ? 'default' : 'danger') ) %}
                                <button type="button" class="btn btn-xs {{ "btn-#{envClass}" }}">{{ env[0:4]|capitalize }}</button>
                            {% endfor %}
                        </div>
                    </td>
                    <td class="text-center" data-order="{{ hit._source._finalization_datetime }}">
                        <i class="fa fa-info-circle text-primary" data-toggle="tooltip" data-placement="top" data-html="true"  aria-hidden="true" title="{{ info }}"></i>
                    </td>
                    {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}
                        <td class="text-center">
                            <div class="btn-group">
                                {% if is_granted('ROLE_COPY_PASTE') %}
                                    <a class="btn btn-xs btn-default" href="{{ path('revision.copy', {'environment': contentType.environment.name, 'type': contentType.name, 'ouuid': hit._id } ) }}">
            							<i class="fa fa-copy"></i>
            						</a>
        						{% endif %}
        						{% if is_granted('ROLE_ADMIN') %}
            						<a class="btn btn-xs btn-default" href="{{ path('data.view', {'environmentName': contentType.environment.name, 'type': contentType.name, 'ouuid':  hit._id} ) }}">
            							<i class="fa fa-file-text"></i>
            						</a>
        						{% endif %}
    						</div>
                        </td>
                    {% endif %}
                </tr>
            {% endfor %}
        </tbody>
    </table>
</div>

{%- set languages = {'nl': 'Dutch', 'fr': 'French', 'de': 'German', 'en': 'English'} -%}
{%- set selectedLanguages = app.request.query.get('l', ['nl']) -%}

<div class="row">
    <div class="col-sm-12 pb-5">
        <form method="GET" id="form-filters" class="form-inline">
            <div class="form-group">
                 <label for="select-langauge">Languages</label>
                <select name="l[]" id="select-langauge" class="selectpicker form-control input-sm" multiple>
                    {%- for l, language in languages -%}
                        <option value="{{ l }}" {{ l in selectedLanguages ? 'selected' }}>{{ language }}</option>
                    {%- endfor -%}
                </select>
            </div>
        </form>
    </div>
</div>


{% set columns = [] %}
{% set columns = columns|merge([
    {
        "label": "Key",
        "template": '<a href="' ~ "{{path('data.revisions', {ouuid: data.id, type: data.contentType} ) }}"~'">' ~"{{ data.source.key }}</a>",
        "orderField": "key"
    }
]) %}

{% for l, lang in languages|filter((v, k) => k in selectedLanguages) %}
    {% set columns = columns|merge([
        {
            "label": (lang),
            "template": "<span class="~'"'~"{% if data.source.label_#{l} is not defined %}text-gray{% endif %}"~'"'~">{{ data.source.label_#{l}|default('N/A') }}</span>",
            "orderField": "label_#{l}.alpha_order"
        }
    ]) %}
{% endfor %}

{% set columnLive %}{% verbatim %}
    <i style="color: {{ docInfo.published('live') and docInfo.aligned('live') ? 'green' : 'red' }}" 
    class="{{ docInfo.published('live') and docInfo.aligned('live') ? 'fa fa-check' : 'fa fa-eye-slash' }}"></i>
{% endverbatim %}{% endset %}

{% set columns = columns|merge([
    {
        "label": "Last Update", 
        "template": '
            <div style="width: 130px;" data-toggle="tooltip" data-placement="top" aria-hidden="true" title="By {{ data.source._finalized_by }}" >
                {{ data.source._finalization_datetime|default ? data.source._finalization_datetime|date("d/m/Y (H:i)") : "N.A." }}
            </div>
        ', 
        "orderField": "_finalization_datetime"
    },
    { "label": "Live", "template": columnLive|format },
    {
        "label": "Action",
        "template": '
            <div class="btn-group btn-group-sm" style="width: 130px;">
              <a href="{{ docInfo.draft ? path("ems_revision_edit", {revisionId: revisionId}) : docInfo.aligned("live") ? path("revision.new-draft", {type: data.contentType, ouuid: data.id }) : path("revision.publish_to", {revisionId: revisionId, envId: liveId, redirectToUrl: path("data.customindexview", {viewId: '~view.id~'})}) }}" class="btn btn-default" style="width: 100px;">
                {{ docInfo.draft ? "Edit draft" : docInfo.aligned("live") ? "Edit" : "Publish in live" }}
              </a>
              <button type="button" class="btn btn-default dropdown-toggle" data-toggle="dropdown" aria-expanded="false">
                <span class="caret">
                </span>
                <span class="sr-only">Toggle Dropdown
                </span>
              </button>
              <ul class="dropdown-menu" role="menu">
                <li>
                  <a href="{{ path("data.revisions", {ouuid: data.id, type: data.contentType} ) }}">Revision</a>
                </li>
                {% if docInfo.draft %}
                    <li><a href="{{ path("revision.publish_to", {revisionId: revisionId, envId: previewId}) }}">Publish in preview</a></li>
                {% elseif not docInfo.aligned(\'live\') %}
                    <li><a href="{{ path("revision.new-draft", {type: data.contentType, ouuid: data.id }) }}">Edit</a></li>
                {% endif %}
              </ul>
            </div>
        ',
        "orderField": "_finalization_datetime" 
    }
]) %}

{% set must = {} %}
{% set filterQuery = [ {
  "multi_match": {
    "fields": [
      "live_search",
      "live_search._2gram",
      "live_search._3gram"
    ],
    "query": "%query%",
    "operator": "and", 
    "type": "bool_prefix"
 } } ] %}
 
{{ emsco_datatable(['preview'],[contentType.name], {
    "frontendOptions": { "pageLength": 100, "order": [[(columns|length - 1), 'desc']] },
    "empty_query": must,
    "query": { "bool": { "must": must|merge(filterQuery) } },
    "row_context": "{% set docInfo = [line.data.contentType, line.data.id]|join(':')|emsco_document_info %}{% set revisionId = line.data.id|get_revision_id(line.data.contentType) %}{% set liveId = 3 %}{% set previewId = 2 %}",
    "columns": columns
}) }}
